import { useState } from "react";
import { convertToSQL } from "./services/api";

function App() {
  const [question, setQuestion] = useState("");
  const [response, setResponse] = useState(null);

  const handleConvert = async () => {
    if (!question.trim()) return;

    try {
      const data = await convertToSQL(question);
      setResponse(data);
    } catch (err) {
      console.error(err);
      setResponse({ sql: "Error", result: "Backend error" });
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center px-4">
      <h1 className="text-4xl font-bold text-blue-600 mb-6">ðŸ§  NL2SQL Converter</h1>

      <div className="w-full max-w-xl bg-white p-6 rounded-xl shadow">
        <textarea
          className="w-full border border-gray-300 p-3 rounded-md resize-none focus:outline-none focus:ring focus:ring-blue-400"
          rows={4}
          placeholder="Type your question here..."
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />

        <button
          className="mt-4 w-full bg-blue-600 text-white py-2 rounded-md hover:bg-blue-700 transition"
          onClick={handleConvert}
        >
          Convert to SQL
        </button>

        {response && (
          <div className="mt-6">
            <h2 className="text-lg font-semibold mb-2">ðŸ“„ Generated SQL:</h2>
            <pre className="bg-gray-100 p-4 rounded text-sm overflow-auto">
              {response.sql}
            </pre>

            <h2 className="text-lg font-semibold mb-2 mt-4">ðŸ“Š Result:</h2>
            <pre className="bg-gray-100 p-4 rounded text-sm overflow-auto">
              {JSON.stringify(response.result, null, 2)}
            </pre>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
