import React, { useState } from 'react';
import InputBox from './components/InputBox';
import ConvertButton from './components/ConvertButton';
import OutputBox from './components/OutputBox';
import { convertToSQL } from './services/api';
import bg from './assets/background.jpg';
import './App.css';

function App() {
  const [input, setInput] = useState('');
  const [sql, setSql] = useState('');

  const handleConvert = async () => {
    if (!input.trim()) return alert('Please enter a query');
    const result = await convertToSQL(input);
    setSql(result);
  };

  return (
    <div className="app-container" style={{ backgroundImage: `url(${bg})` }}>
      <div className="app-box">
        <h1>üìù Natural Language ‚Üí SQL Converter</h1>
        <InputBox input={input} setInput={setInput} />
        <ConvertButton handleConvert={handleConvert} />
        <OutputBox sql={sql} />
      </div>
    </div>
  );
}

export default App;
